function updateParentSelect(e,t){t.empty().append('<option value="0" selected>请选择父级知识点</option>'),0>=e||$.ajax({url:getParentNodeLink,type:"GET",dataType:"json",data:{chapterId:e},success:function(e){$.each(e,function(e,n){t.append('<option value="'+n.id+'">'+n.name+"</option>")})},error:function(){alert("sorry,something error")}})}function updateChildrenSelect(e,t){t.empty().append('<option value="0" selected>请选择子级知识点</option>'),0>=e||$.ajax({url:getChildrenNodeLink,type:"GET",dataType:"json",data:{parentId:e},success:function(e){$.each(e,function(e,n){t.append('<option value="'+n.id+'">'+n.name+"</option>")})},error:function(){alert("sorry,something error")}})}$(function(){$("#chapterSelect").change(function(){var e=$(this),t=e.children("option:selected").val(),n=$("#parentSelect");updateParentSelect(t,n)})});